import { EVMWalletClient } from './evm.js';
import { EVMSmartWalletClient } from './evm-smart-wallet.js';
import { SolanaWalletClient } from './solana.js';
import { WalletClient, EVMChain, SolanaChain, Chain } from './core.js';
import 'abitype';
import '@solana/web3.js';

type ChainForWalletClient<TWalletClient extends WalletClient> = TWalletClient extends EVMWalletClient ? EVMChain : TWalletClient extends SolanaWalletClient ? SolanaChain : Chain;
declare function isEVMChain(chain: Chain): chain is EVMChain;
declare function isSolanaChain(chain: Chain): chain is SolanaChain;
type AnyEVMWalletClient = EVMWalletClient | EVMSmartWalletClient;

export { type AnyEVMWalletClient, type ChainForWalletClient, isEVMChain, isSolanaChain };
